jQuery(function() {
	initMobileNav();
	initOpenLighbox();
	initFancybox();
	initCustomForms();
	initAccordion();
	initToggler();
	initFormValidation();
	initLanguage();
	initIframeChange();
});

// Used to remove popup window asking if really want to lave when changing iframe URL
window.onbeforeunload = null;

window.base_url = 'https://app.mapx.org/?lockProject=true&';
window.country = 'MX-XNI-RMZ-KKL-FMS-DVH'
window.themes = [];

function initOpenLighbox() {
	var elem = jQuery('#popup-on-load');
	if( elem.length >= 1) {
		var btnOpen = jQuery('<a href="#popup-on-load" class="lightbox"></a>');

		jQuery('body').append(btnOpen);
		jQuery(window).on('load', function(){
			btnOpen.trigger('click');
		});
	}
}

function initIframeChange() {
	var iframe = jQuery('[iframe]');

	jQuery('#region').on('change', function(){
		window.country = jQuery(this).val();
    current_url = iframe.attr('src');
    url = window.base_url + 'project=' + window.country;

    if(window.themes.length > 0) {
      url += '&collections=' + window.themes + '&noViews=false';
    }
    else {
      url += '&noViews=true';
    }

		iframe.attr('src', url);
	});


	jQuery('[toggle]').on('click', function(e){
		e.preventDefault();
		toggle = jQuery(this);
		theme = toggle.attr('data-theme');
		selected = toggle.attr('data-selected');

		selected == 'true' ? selected = 'false' : selected = 'true';
		toggle.attr('data-selected', selected);

		arrayIndex = jQuery.inArray(encodeURI(theme), window.themes);

		if(selected == 'true' && arrayIndex == -1) {
			window.themes.push(encodeURI(theme));
		}

		if(selected == 'false' && arrayIndex > -1) {
			window.themes.splice(arrayIndex, 1);
		}

    url = window.base_url + 'project=' + window.country;

    if(window.themes.length > 0) {
      collections = window.themes.join(',');
      url += '&collections=' + collections + '&noViews=false';
    }
    else {
      url += '&noViews=true'
    }

		iframe.attr('src', url);
	});
}

function initLanguage() {
	var elem = jQuery('.languages').find('a');
	var item = jQuery('.language');
	elem.each(function() {
		var self = jQuery(this);
		self.on('click', function() {
			var itemText = jQuery(this).text();
			item.text(itemText);
		});
	});
}

function initToggler() {
	var elem = jQuery('.switch');

	elem.each(function() {
		var self = jQuery(this);
		self.on('click',function() {
			jQuery(this).parent().toggleClass('active');
		});
	});
}

function initFormValidation() {
	jQuery('.form-validation').formValidation({
		errorClass: 'error',
		addClassToParent: '.input-wrap',
		successSendClass: 'ajax-success'
	});
}

// initialize custom form elements
function initCustomForms() {
	jcf.setOptions('Select', {
		maxVisibleItems: 5,
		wrapNative: false,
		wrapNativeOnMobile: false,
	});
	jcf.replaceAll();
}


// mobile menu init
function initMobileNav() {
	jQuery('body').mobileNav({
		menuActiveClass: 'sidebar-active',
		menuOpener: '.js-sidebar-opener'
	});
	jQuery('.language-selector').mobileNav({
		menuActiveClass: 'active',
		menuOpener: '.language-opener',
		hideOnClickOutside: true,
		menuDrop: '.drop'
	});
}

// lightbox init
function initFancybox() {
	jQuery('a.lightbox, [data-fancybox]').fancybox({
		parentEl: 'body',
		margin: [50, 0],
		clickSlide: false,
		clickOutside: false,
		closeClickOutside: false,
		afterShow: function(instance, slide){
			slide.$content.find('.btn').on('click', function(e){
				e.preventDefault();
				jQuery('#content iframe').attr('src', jQuery(this).attr('data-src'));
				jQuery.fancybox.close();
			});
		},
		beforeShow: function(instance, slide){
			jQuery('.info-list').find('a').parent().removeClass('active');
			jQuery('.info-list').find('a[href="' + slide.src + '"]').parent().addClass('active');
			jQuery('.btn-wrap').find('a').removeClass('active');
			jQuery('.btn-wrap').find('a[href="' + slide.src + '"]').addClass('active');
		},
		afterClose: function(instance, slide){
			jQuery('.info-list').find('a').parent().removeClass('active');
			jQuery('.btn-wrap').find('a').removeClass('active');
		}
	});
}

// accordion menu init
function initAccordion() {
	jQuery('.accordion').slideAccordion({
		opener: '.accordion-opener',
		slider: '.slide',
		animSpeed: 300,
		addClassBeforeAnimation: true
	});
}

